                  </div>
                </div>
              </div>
            </div>
            <Link 
              href={`/${country}/about`} 
              className={cn(
                "text-foreground hover:text-primary transition-colors duration-200",
                isActive("/about") && "text-primary"
              )}
              data-testid="nav-about"
            >
              {t.nav.about}
