@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: #f0f0f0;
  --foreground: #1a1a1a;
  --card: #fcfcfc;
  --card-foreground: hsl(0, 0%, 0%);
  --popover: hsl(45, 25%, 97%);
  --popover-foreground: hsl(0, 0%, 100%);
  --primary: #b91c2e;
  --primary-foreground: #ffffff;
  --secondary: #c4c4c4;
  --secondary-foreground: #1a1a1a;
  --muted: #e3e3e3;
  --muted-foreground: #212121;
  --accent: hsl(0, 0%, 100%);
  --accent-foreground: hsl(0, 0%, 10.2%);
  --destructive: hsl(0, 0%, 0%);
  --destructive-foreground: #ffffff;
  --border: #e8e8e8;
  --input: #b5b5b5;
  --ring: hsl(0, 0%, 41.96%);
  --chart-1: hsl(203.8863, 88.2845%, 53.1373%);
  --chart-2: hsl(159.7826, 100%, 36.0784%);
  --chart-3: hsl(42.0290, 92.8251%, 56.2745%);
  --chart-4: hsl(147.1429, 78.5047%, 41.9608%);
  --chart-5: hsl(341.4894, 75.2000%, 50.9804%);
  --sidebar: hsl(45, 25%, 97%);
  --sidebar-foreground: hsl(20, 14%, 17%);
  --sidebar-primary: hsl(352, 72%, 42%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(25, 45%, 80%);
  --sidebar-accent-foreground: hsl(20, 14%, 17%);
  --sidebar-border: hsl(45, 15%, 80%);
  --sidebar-ring: hsl(352, 72%, 42%);
  --font-sans: 'Poppins', sans-serif;
  --font-serif: 'Poppins', sans-serif;
  --font-mono: Menlo, monospace;
  --radius: 0.8rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(352, 72%, 42% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(352, 72%, 42% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(352, 72%, 42% / 0.00), 0px 1px 2px -1px hsl(352, 72%, 42% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(352, 72%, 42% / 0.00), 0px 1px 2px -1px hsl(352, 72%, 42% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(352, 72%, 42% / 0.00), 0px 2px 4px -1px hsl(352, 72%, 42% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(352, 72%, 42% / 0.00), 0px 4px 6px -1px hsl(352, 72%, 42% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(352, 72%, 42% / 0.00), 0px 8px 10px -1px hsl(352, 72%, 42% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(352, 72%, 42% / 0.00);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

.dark {
  --background: hsl(20, 14%, 4%);
  --foreground: hsl(45, 25%, 91%);
  --card: hsl(20, 14%, 8%);
  --card-foreground: hsl(45, 25%, 85%);
  --popover: hsl(20, 14%, 4%);
  --popover-foreground: hsl(45, 25%, 91%);
  --primary: hsl(352, 72%, 42%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(30, 15%, 52%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(20, 14%, 15%);
  --muted-foreground: hsl(45, 15%, 46%);
  --accent: hsl(25, 45%, 20%);
  --accent-foreground: hsl(45, 25%, 85%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(20, 14%, 15%);
  --input: hsl(20, 14%, 18%);
  --ring: hsl(352, 72%, 42%);
  --chart-1: hsl(203.8863, 88.2845%, 53.1373%);
  --chart-2: hsl(159.7826, 100%, 36.0784%);
  --chart-3: hsl(42.0290, 92.8251%, 56.2745%);
  --chart-4: hsl(147.1429, 78.5047%, 41.9608%);
  --chart-5: hsl(341.4894, 75.2000%, 50.9804%);
  --sidebar: hsl(20, 14%, 8%);
  --sidebar-foreground: hsl(45, 25%, 85%);
  --sidebar-primary: hsl(352, 72%, 42%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(25, 45%, 20%);
  --sidebar-accent-foreground: hsl(45, 25%, 85%);
  --sidebar-border: hsl(20, 14%, 15%);
  --sidebar-ring: hsl(352, 72%, 42%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

.slider-container {
  position: relative;
  overflow: hidden;
}

.slide {
  opacity: 0;
  transform: translateX(100%);
  transition: all 0.6s ease-in-out;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

.slide.active {
  opacity: 1;
  transform: translateX(0);
  position: relative;
}

.dropdown {
  opacity: 0;
  visibility: hidden;
  transform: translateY(-10px);
  transition: all 0.3s ease;
}

.dropdown.show {
  opacity: 1;
  visibility: visible;
  transform: translateY(0);
}

.counter {
  font-size: 3rem;
  font-weight: 700;
  color: var(--primary);
}

.animate-fadeInUp {
  animation: fadeInUp 0.8s ease-out;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.hover-scale {
  transition: transform 0.3s ease;
}

.hover-scale:hover {
  transform: scale(1.05);
}

.blog-card {
  transition: all 0.3s ease;
}

.blog-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 20px 40px rgba(0,0,0,0.1);
}

.client-logo {
  filter: grayscale(100%);
  transition: filter 0.3s ease;
}

.client-logo:hover {
  filter: grayscale(0%);
}

